        <script>
            (function() {
              const element = document.getElementById("webinar-date");
              if (!element) return;
          
              const displayStartDate = new Date(Date.UTC(2025, 5, 1)); 
              const weekStepStart = new Date(Date.UTC(2025, 4, 31));   
          
              const now = new Date();
              const nowUTC = new Date(Date.UTC(now.getFullYear(), now.getMonth(), now.getDate()));
          
              let resultDate;
          
              if (nowUTC < weekStepStart) {
                resultDate = displayStartDate;
              } else {
                const msInWeek = 7 * 86400000;
                const diffWeeks = Math.floor((nowUTC - weekStepStart) / msInWeek) + 1;
                resultDate = new Date(displayStartDate.getTime() + diffWeeks * msInWeek);
              }
          
              const months = [
                "января", "февраля", "марта", "апреля", "мая", "июня",
                "июля", "августа", "сентября", "октября", "ноября", "декабря"
              ];
          
              const day = resultDate.getUTCDate();
              const month = months[resultDate.getUTCMonth()];
          
              element.innerHTML = `<span style="color: rgb(175, 175, 175)">Дата:</span>${day} ${month}`;
            })();
          </script>

